class AndroidAudioPlayer(
 private val applicationScope: CoroutineScope
) : AudioPlayer {
 private val _activeTrack = MutableStateFlow<AudioTrack?>(null)
 override val activeTrack: StateFlow<AudioTrack?> = _activeTrack.asStateFlow()

 private var mediaPlayer: MediaPlayer? = null

 private var durationJob: Job? = null

 override fun play(filePath: String, onComplete: () -> Unit) {
 stop()

 mediaPlayer = MediaPlayer().apply {
 val fileInputStream = FileInputStream(File(filePath))
 try {
 setDataSource(fileInputStream.fd)

 prepare()
 start()

 _activeTrack.update {
 AudioTrack(
 totalDuration = this.duration.milliseconds,
 durationPlayed = Duration.ZERO,
 isPlaying = true
 )
 }
 trackDuration()

 setOnCompletionListener {
 onComplete()
 stop()
 }
 } catch (e: Exception) {
 Timber.e(e, "Error playing audio")
 } finally {
 fileInputStream.close()
 }
 }
 }

 override fun pause() {
 if (activeTrack.value?.isPlaying != true) return

 _activeTrack.update {
 it?.copy(isPlaying = false)
 }
 durationJob?.cancel()
 mediaPlayer?.pause()
 }

 override fun resume() {
 if (activeTrack.value?.isPlaying != false) return

 _activeTrack.update {
 it?.copy(isPlaying = true)
 }
 mediaPlayer?.start()
 trackDuration()
 }

 override fun stop() {
 _activeTrack.update {
 it?.copy(isPlaying = false, durationPlayed = Duration.ZERO)
 }
 durationJob?.cancel()
 mediaPlayer?.apply {
 stop()
 reset()
 release()
 }
 mediaPlayer = null
 }

 private fun trackDuration() {
 durationJob?.cancel()
 durationJob = applicationScope.launch {
 do {
 _activeTrack.update {
 it?.copy(
 durationPlayed = mediaPlayer?.currentPosition?.milliseconds ?: Duration.ZERO
 )
 }
 delay(10L)
 } while (activeTrack.value?.isPlaying == true && mediaPlayer?.isPlaying == true)
 }
 }
